<script>

        function editComment(CommentId, UserId, PostId, CommentDate, IsDeleted) {
        
        var Content = $('#new_comment').val();
        console.log(Content);
        $.ajax({
                type: 'POST',
                url: '/Comments/Edit',
            data: {
                CommentId: CommentId,
                UserId: UserId,
                PostId: PostId,
                CommentDate:CommentDate,
                Content: Content,
                IsDeleted: IsDeleted,
                },                                
                dataType: 'json',
                success: function (response) {
                    refreshPost();
                    $('#editCommentModal').modal('toggle');
                },

                error: function (response) {
                    alert(""Error"");
                }
            });
        }


</script>
